char pcpropColumnDefs[NUMBER_OF_AA]={'\0'};

//Selected PCProps name should be listed here
char **pcpropSelectedNames = NULL ;//This should be freeed before exit

//Number of selectedProps;
int pcpropSelectionCount = 0;

//Mapping from amino acid symbols to the pcprop matrix column index
int pcpropColDefMap[NUMBER_OF_LETTERS]={0};

//Default pcproperties selection for classic and amphiphilic selection
ifTextFile *pcpropClassicSelection;
ifTextFile *pcpropAmphiphilicSelection;

//Convert the AAIndex Text Database to a hash in memory,using uthash for POSIX C
pcprop *pcpropLoadMemFile()
{
    char *curStart = pcpropMemFile;
    pcprop *pclist_handle = NULL;

    //Parse the first line as header of file
    bool lastline = false;
    char *nextline = NULL;
    nextline = pdulibParseTextLines(curStart, &lastline);
    //Here we get the amino acid order in the list
    strtok(curStart,",");
    //char *orderHead = strtok(curStart,",");
    int orderIdx = 0;
    while (char *orderField = strtok(NULL,","))
    {
        pcpropColumnDefs[orderIdx] = orderField[0];
        orderIdx ++;
        if (orderIdx >=20)
            break;
    }
    curStart = nextline + 1;
    //Now, lets dance with the rest of the file
    while (!lastline)
    {
        char *nextline = pdulibParseTextLines(curStart, &lastline);
        pcprop *tmpItem = (pcprop*)calloc(sizeof(pcprop),1);
        tmpItem->pcname = strtok(curStart,",");
        tmpItem->pcvalue = (decimal*)calloc(sizeof(decimal),NUMBER_OF_AA);
        int k = 0;
        while (char *field = strtok(NULL,","))
        {
                //Here we use POSIX style conversion, for portability to WIN32 platform as well as the iMac
                //Actually, I really hate Apple. Because I hate people biting my apple.

                #ifdef __USE_DOUBLE__
                tmpItem->pcvalue[k] = (decimal)strtod(field,NULL);
                #else
                tmpItem->pcvalue[k] = (decimal)strtof(field,NULL);
                #endif
                k++;
                if (k >= NUMBER_OF_AA)
                    break;

        }
        tmpItem->pcdesc = strtok(NULL,"\0"); //The description field could contain some "," , so we have to use other delimiters.
        //Adding items to hash table
        HASH_ADD_KEYPTR(hh,pclist_handle,tmpItem->pcname,strlen(tmpItem->pcname),tmpItem);
        //Process from next curStart
        curStart = nextline +1;
    }
    return pclist_handle;
}

void pcpropUnloadMemFile(pcprop *pchh)
{
    //Iteration to clear all items in hash
    pcprop *t, *p;
    HASH_ITER(hh,pchh,t,p)
    {
        HASH_DEL(pchh,t);
        free(t);
        t=NULL;
    }
}

void pcpropPrintMemFile(pcprop *pchh)
{
    pcprop *t, *p;
    HASH_ITER(hh,pchh,t,p)
    {
        printf("%s\t%s\n",t->pcname,t->pcdesc);
    }
}

bool pcpropIsNameExist(pcprop *pchh, char *t)
{
    pcprop *tmpResult = NULL;
    HASH_FIND_STR(pchh, t, tmpResult);
    if (tmpResult) return true;
    else return false;
}

ifTextFile *pcpropLoadSelectionFile(parameters *p)
{

    if (p->pcselfile)
    {
        ifTextFile *pcsels = ifLoadTextFile(p->pcselfile);
        return pcsels;
    }
    else
    {
        if (p->pse == PSE_CLASSIC)
        {
            return pcpropClassicSelection;
        }
        else if (p->pse == PSE_AMPHIPHILIC)
        {
            return pcpropAmphiphilicSelection;
        }
        else
        {
            return pcpropClassicSelection;
        }
    }
}

void pcpropUnloadSelectionFile(ifTextFile *t, parameters *p)
{
    if (p->pcselfile)
    {
        ifUnloadTextFile(t);
    }
    else
    {
        ifUnloadTextFile(pcpropClassicSelection);
        ifUnloadTextFile(pcpropAmphiphilicSelection);
        pcpropClassicSelection=NULL;
        pcpropAmphiphilicSelection=NULL;
    }
}

void pcpropSelectByFile(pcprop *pchh, struct parameters *p, decimal ***refout_selectMatrix)
{
    char **tmprefout_selectedNames = NULL;
    decimal **tmprefout_selectMatrix = NULL;

    ifTextFile *pcsels = pcpropLoadSelectionFile(p);

    int cnt_sels = pcsels->cntLines;
    tmprefout_selectedNames = (char**)calloc(sizeof(char*),cnt_sels);
    tmprefout_selectMatrix = (decimal**)calloc(sizeof(decimal*),cnt_sels);
    for (int i = 0 ; i < cnt_sels ; i++)
    {
        if (pcpropIsNameExist(pchh,pcsels->lines[i]))
        {
            pcprop *tmpProp = NULL;
            HASH_FIND_STR(pchh,pcsels->lines[i],tmpProp);
            tmprefout_selectedNames[i]=tmpProp->pcname;
            pdulibNormalize(tmpProp->pcvalue,NUMBER_OF_AA);
            tmprefout_selectMatrix[i]=tmpProp->pcvalue;

        }
        else
        {
            emProcess(EK_ABORT_BAD_PCPROP,pcsels->lines[i],p->pcselfile,i);
        }
    }
    //*refout_selectedNames = tmprefout_selectedNames;
    pcpropSelectedNames = tmprefout_selectedNames;
    *refout_selectMatrix = tmprefout_selectMatrix;
    pcpropSelectionCount = cnt_sels;
    pcpropUnloadSelectionFile(pcsels,p);
}

void pcpropConvertSingleSeqToProperties(struct prseq *t, decimal **selectedPropsMatrix)
{
    t->propmatrix = (decimal**)calloc(sizeof(decimal*),pcpropSelectionCount);
    for (int  i = 0 ; i < pcpropSelectionCount ; i++)
    {
        t->propmatrix[i]=(decimal*)calloc(sizeof(decimal), t->len_seq);
        for (int j = 0 ; j < t->len_seq ; j++)
        {
            //Now, this is the magic of pcpropColDefMap array
            int tidx = pcpropColDefMap[(int)(toupper(t->seq[j]) - 'A')];
            if (tidx >= 0)
            {
                t->propmatrix[i][j]=selectedPropsMatrix[i][tidx];
            }
            else
            {
                t->propmatrix[i][j]=0;
                emProcess(EK_WARN_INVALID_SYMBOL,toupper(t->seq[j]),t->id,j);
            }

        }
    }
}

void pcpropCleanUpSinggleSeqProps(struct prseq *t)
{
    for (int i = 0 ; i < pcpropSelectionCount ;i++)
    {
        free(t->propmatrix[i]);
        t->propmatrix[i] = NULL;
    }
    free(t->propmatrix);
    t->propmatrix = NULL;
}

